import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate, useLocation } from 'react-router-dom';
import Navbar from './components/Navbar';
import Hero from './components/Hero';
import VisionMission from './components/VisionMission';
import Features from './components/Features';
import Testimonials from './components/Testimonials';
import Gallery from './components/Gallery';
import BookingForm from './components/BookingForm';
import Footer from './components/Footer';
import LoginRegister from './components/LoginRegister';
import Matches from './components/Matches';
import Profile from './components/Profile';

const initialMatches = [
  {
    id: 1,
    date: '2024-06-10',
    time: '6:00 PM',
    location: 'Andheri West Turf',
    playersLeft: 22,
    price: '₹299/player',
  },
  {
    id: 2,
    date: '2024-06-11',
    time: '7:00 PM',
    location: 'Bandra East Elite Turf',
    playersLeft: 22,
    price: '₹399/player',
  },
  {
    id: 3,
    date: '2024-06-12',
    time: '8:00 PM',
    location: 'Powai Pro Turf',
    playersLeft: 22,
    price: '₹499/player',
  },
  {
    id: 4,
    date: '2024-06-13',
    time: '6:30 PM',
    location: 'Juhu Champion Turf',
    playersLeft: 22,
    price: '₹299/player',
  },
  {
    id: 5,
    date: '2024-06-14',
    time: '7:30 PM',
    location: 'Andheri West Turf',
    playersLeft: 22,
    price: '₹399/player',
  },
];

const initialTestimonials = [];

const AppRoutes = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [pendingMatch, setPendingMatch] = useState(null);
  const [matches, setMatches] = useState(initialMatches);
  const [testimonials, setTestimonials] = useState(initialTestimonials);
  const [isAdmin, setIsAdmin] = useState(false);
  const [currentUser, setCurrentUser] = useState(null);
  const [showAdminLogin, setShowAdminLogin] = useState(false);
  const [adminPassword, setAdminPassword] = useState('');
  const [showLoginModal, setShowLoginModal] = useState(false);
  const [showProfile, setShowProfile] = useState(false);
  const [userBookings, setUserBookings] = useState([]);
  const navigate = useNavigate();

  const handleBook = (match) => {
    if (!isLoggedIn) {
      setShowLoginModal(true);
      setPendingMatch(match);
    } else {
      // Decrement playersLeft for the booked match
      setMatches((prevMatches) =>
        prevMatches.map((m) =>
          m.id === match.id && m.playersLeft > 0
            ? { ...m, playersLeft: m.playersLeft - 1 }
            : m
        )
      );

      // Add booking to user's bookings
      const newBooking = {
        id: Date.now(),
        matchId: match.id,
        match: match,
        userId: currentUser.id,
        bookingTime: new Date().toISOString(),
        status: 'confirmed'
      };
      setUserBookings(prev => [...prev, newBooking]);

      // Redirect to payment gateway
      navigate('/payment');
    }
  };

  const handleCancelBooking = (bookingId) => {
    const booking = userBookings.find(b => b.id === bookingId);
    if (!booking) return;

    const bookingTime = new Date(booking.bookingTime);
    const currentTime = new Date();
    const hoursDifference = (currentTime - bookingTime) / (1000 * 60 * 60);

    if (hoursDifference > 24) {
      alert('Sorry, you can only cancel bookings within 24 hours. No refund will be provided.');
      return;
    }

    // Increment playersLeft for the cancelled match
    setMatches((prevMatches) =>
      prevMatches.map((m) =>
        m.id === booking.matchId
          ? { ...m, playersLeft: m.playersLeft + 1 }
          : m
      )
    );

    // Remove booking from user's bookings
    setUserBookings(prev => prev.filter(b => b.id !== bookingId));

    alert('Booking cancelled successfully! Full refund will be processed within 3-5 business days.');
  };

  const getUserBookings = (matchId) => {
    return userBookings.filter(b => b.matchId === matchId && b.userId === currentUser?.id);
  };

  const isUserBooked = (matchId) => {
    return userBookings.some(b => b.matchId === matchId && b.userId === currentUser?.id);
  };

  const handleShowLogin = () => {
    setShowLoginModal(true);
  };

  const handleShowProfile = () => {
    setShowProfile(true);
  };

  const handleUpdateProfile = (profileData) => {
    setCurrentUser(prev => ({
      ...prev,
      ...profileData
    }));
    setShowProfile(false);
    alert('Profile updated successfully!');
  };

  const handleLogin = () => {
    setIsLoggedIn(true);
    setCurrentUser({ id: Date.now(), name: 'User' }); // This will be updated when we get user data
    if (pendingMatch) {
      setMatches((prevMatches) =>
        prevMatches.map((m) =>
          m.id === pendingMatch.id && m.playersLeft > 0
            ? { ...m, playersLeft: m.playersLeft - 1 }
            : m
        )
      );

      // Add booking to user's bookings
      const newBooking = {
        id: Date.now(),
        matchId: pendingMatch.id,
        match: pendingMatch,
        userId: Date.now(), // Using the same ID as currentUser
        bookingTime: new Date().toISOString(),
        status: 'confirmed'
      };
      setUserBookings(prev => [...prev, newBooking]);

      // Redirect to payment gateway
      navigate('/payment');
      setPendingMatch(null);
    } else {
      navigate('/');
    }
    setShowLoginModal(false);
  };

  const handleLoginWithData = (userData) => {
    const userId = Date.now();
    setIsLoggedIn(true);
    setCurrentUser({ 
      id: userId,
      name: userData.fullName,
      age: userData.age,
      phone: userData.phone,
      firstName: userData.fullName.split(' ')[0] || '',
      lastName: userData.fullName.split(' ').slice(1).join(' ') || '',
      email: '',
      dob: ''
    });
    if (pendingMatch) {
      setMatches((prevMatches) =>
        prevMatches.map((m) =>
          m.id === pendingMatch.id && m.playersLeft > 0
            ? { ...m, playersLeft: m.playersLeft - 1 }
            : m
        )
      );

      // Add booking to user's bookings
      const newBooking = {
        id: Date.now(),
        matchId: pendingMatch.id,
        match: pendingMatch,
        userId: userId,
        bookingTime: new Date().toISOString(),
        status: 'confirmed'
      };
      setUserBookings(prev => [...prev, newBooking]);

      // Redirect to payment gateway
      navigate('/payment');
      setPendingMatch(null);
    } else {
      navigate('/');
    }
    setShowLoginModal(false);
  };

  const handleTestimonialSubmit = (testimonial) => {
    const newTestimonial = {
      ...testimonial,
      id: Date.now(),
      userId: currentUser?.id || null,
      timestamp: new Date().toISOString()
    };
    setTestimonials((prev) => [newTestimonial, ...prev]);
  };

  const handleDeleteTestimonial = (testimonialId) => {
    setTestimonials((prev) => prev.filter(t => t.id !== testimonialId));
  };

  const handleUpdateMatch = (matchId, updatedData) => {
    setMatches((prevMatches) =>
      prevMatches.map((match) =>
        match.id === matchId
          ? { ...match, ...updatedData }
          : match
      )
    );
  };

  const toggleAdminMode = () => {
    if (isAdmin) {
      // Exit admin mode
      setIsAdmin(false);
      setShowAdminLogin(false);
      setAdminPassword('');
    } else {
      // Request admin password
      setShowAdminLogin(true);
    }
  };

  const handleAdminLogin = () => {
    // Simple admin password - in production, this should be more secure
    if (adminPassword === 'kickora2024') {
      setIsAdmin(true);
      setShowAdminLogin(false);
      setAdminPassword('');
      alert('Admin mode activated!');
    } else {
      alert('Incorrect admin password!');
      setAdminPassword('');
    }
  };

  const handleAdminCancel = () => {
    setShowAdminLogin(false);
    setAdminPassword('');
  };

  return (
    <>
      <Navbar 
        isMenuOpen={false} 
        setIsMenuOpen={() => {}} 
        isAdmin={isAdmin} 
        onToggleAdmin={toggleAdminMode}
        showAdminLogin={showAdminLogin}
        adminPassword={adminPassword}
        setAdminPassword={setAdminPassword}
        onAdminLogin={handleAdminLogin}
        onAdminCancel={handleAdminCancel}
        isLoggedIn={isLoggedIn}
        currentUser={currentUser}
        onShowProfile={handleShowProfile}
      />

      {/* Login Modal */}
      {showLoginModal && (
        <div className="fixed inset-0 bg-emerald bg-opacity-90 flex items-center justify-center z-50">
          <div className="bg-cream rounded-2xl shadow-xl p-8 w-full max-w-md mx-4">
            <div className="flex justify-between items-center mb-6">
              <h2 className="text-2xl font-bold text-emerald">Login Required</h2>
              <button
                onClick={() => setShowLoginModal(false)}
                className="text-emerald hover:text-emerald-700 transition-colors duration-300"
              >
                <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                </svg>
              </button>
            </div>
            <p className="text-emerald mb-6">
              You need to be logged in to book a match. Please login or register to continue.
            </p>
            <LoginRegister onLogin={handleLoginWithData} />
          </div>
        </div>
      )}

      {/* Profile Page */}
      {showProfile && (
        <Profile 
          user={currentUser} 
          onUpdateProfile={handleUpdateProfile} 
          onBack={() => setShowProfile(false)} 
        />
      )}

      <Routes>
        <Route
          path="/"
          element={
            <div className="App">
              <Hero />
              <VisionMission />
              <Features />
              <Testimonials 
                testimonials={testimonials} 
                onSubmit={handleTestimonialSubmit} 
                onDelete={handleDeleteTestimonial}
                isLoggedIn={isLoggedIn} 
                isAdmin={isAdmin}
                currentUser={currentUser}
              />
              <Gallery />
              <BookingForm />
              <Footer />
            </div>
          }
        />
        <Route path="/login" element={<LoginRegister onLogin={handleLoginWithData} />} />
        <Route path="/matches" element={
          <Matches 
            matches={matches} 
            onBook={handleBook} 
            isAdmin={isAdmin} 
            onUpdateMatch={handleUpdateMatch}
            isLoggedIn={isLoggedIn}
            onShowLogin={handleShowLogin}
            onCancelBooking={handleCancelBooking}
            getUserBookings={getUserBookings}
            isUserBooked={isUserBooked}
          />
        } />
        <Route path="/payment" element={
          <div className="min-h-screen bg-gradient-to-br from-cream to-emerald-50 pt-24 pb-16">
            <div className="container-custom">
              <div className="max-w-2xl mx-auto text-center">
                <h1 className="text-3xl font-bold text-emerald mb-6">Payment Gateway</h1>
                <p className="text-emerald-700 mb-8">
                  This is where the payment gateway will be integrated. You can update this page later.
                </p>
                <button
                  onClick={() => navigate('/matches')}
                  className="btn-primary"
                >
                  Back to Matches
                </button>
              </div>
            </div>
          </div>
        } />
      </Routes>
    </>
  );
};

function App() {
  return (
    <Router>
      <AppRoutes />
    </Router>
  );
}

export default App;
