import React, { useState, useEffect } from 'react';
import { testimonialsAPI } from '../services/api';

const StarRating = ({ rating, setRating, editable }) => (
  <div className="flex items-center space-x-1">
    {[1, 2, 3, 4, 5].map((star) => (
      <button
        key={star}
        type="button"
        className={
          star <= rating
            ? 'text-emerald text-2xl'
            : 'text-emerald-200 text-2xl'
        }
        onClick={editable ? () => setRating(star) : undefined}
        disabled={!editable}
        aria-label={`Rate ${star} star${star > 1 ? 's' : ''}`}
      >
        â˜…
      </button>
    ))}
  </div>
);

const Testimonials = ({ isLoggedIn, isAdmin, currentUser }) => {
  const [testimonials, setTestimonials] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState('');
  const [text, setText] = useState('');
  const [rating, setRating] = useState(5);
  const [name, setName] = useState('');
  const [submitted, setSubmitted] = useState(false);

  // Fetch testimonials on component mount
  useEffect(() => {
    const fetchTestimonials = async () => {
      try {
        setIsLoading(true);
        const data = await testimonialsAPI.getAll();
        setTestimonials(data);
      } catch (error) {
        setError('Failed to load testimonials');
        console.error('Error fetching testimonials:', error);
      } finally {
        setIsLoading(false);
      }
    };

    fetchTestimonials();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (text.trim() && rating) {
      try {
        const userName = isLoggedIn && currentUser ? currentUser.full_name : (name.trim() || 'Anonymous');
        const testimonialData = {
          text: text.trim(),
          rating,
          name: userName,
        };

        await testimonialsAPI.create(testimonialData);

        // Refresh testimonials after submission
        const updatedTestimonials = await testimonialsAPI.getAll();
        setTestimonials(updatedTestimonials);

        setText('');
        setRating(5);
        setName('');
        setSubmitted(true);
        setTimeout(() => setSubmitted(false), 2000);
      } catch (error) {
        setError('Failed to submit testimonial');
      }
    }
  };

  const handleDelete = async (testimonialId) => {
    try {
      await testimonialsAPI.delete(testimonialId);
      // Refresh testimonials after deletion
      const updatedTestimonials = await testimonialsAPI.getAll();
      setTestimonials(updatedTestimonials);
    } catch (error) {
      setError('Failed to delete testimonial');
    }
  };

  const canDeleteTestimonial = (testimonial) => {
    return isAdmin || (isLoggedIn && currentUser && testimonial.user_id === currentUser.id);
  };

  if (isLoading) {
    return (
      <section id="testimonials" className="section-padding bg-cream">
        <div className="container-custom">
          <div className="flex items-center justify-center h-64">
            <div className="text-emerald text-xl">Loading testimonials...</div>
          </div>
        </div>
      </section>
    );
  }

  return (
    <section id="testimonials" className="section-padding bg-cream">
      <div className="container-custom">
        <div className="text-center mb-16">
          <h2 className="text-3xl md:text-4xl font-bold text-emerald mb-4">
            What Our Players Say
          </h2>
          <p className="text-lg text-emerald-700 max-w-2xl mx-auto">
            Real experiences from our growing community of football enthusiasts.
          </p>
        </div>

        {/* Submission Form */}
        <div className="max-w-xl mx-auto mb-12 bg-emerald-50 rounded-2xl p-6 shadow-lg">
            <h3 className="text-xl font-semibold text-emerald mb-4">Share Your Experience</h3>
            <form onSubmit={handleSubmit} className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-emerald mb-1">Your Testimonial</label>
                <textarea
                  value={text}
                  onChange={(e) => setText(e.target.value)}
                  required
                  rows={3}
                  className="w-full px-4 py-2 border border-emerald rounded-lg focus:ring-2 focus:ring-emerald focus:border-transparent transition-colors duration-300 text-emerald bg-cream placeholder-emerald-500"
                  placeholder="Write your experience..."
                />
              </div>
              <div className="flex items-center space-x-4">
                <label className="text-emerald font-medium">Your Rating:</label>
                <StarRating rating={rating} setRating={setRating} editable={true} />
              </div>
              <div>
                <label className="block text-sm font-medium text-emerald mb-1">
                  {isLoggedIn && currentUser ? 'Your Name (from profile)' : 'Your Name (optional)'}
                </label>
                <input
                  type="text"
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                  className="w-full px-4 py-2 border border-emerald rounded-lg focus:ring-2 focus:ring-emerald focus:border-transparent transition-colors duration-300 text-emerald bg-cream placeholder-emerald-500"
                  placeholder={isLoggedIn && currentUser ? currentUser.name : "Enter your name or leave blank for anonymous"}
                  disabled={isLoggedIn && currentUser}
                />
              </div>
              <button type="submit" className="btn-primary w-full text-lg py-2">
                Submit Testimonial
              </button>
              {submitted && <div className="text-emerald-700 text-center mt-2">Thank you for your feedback!</div>}
              {error && <div className="text-red-600 text-center mt-2">{error}</div>}
            </form>
          </div>

        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
          {testimonials.map((testimonial, index) => (
            <div
              key={testimonial.id || index}
              className="bg-emerald-50 rounded-2xl p-8 hover:shadow-lg transition-all duration-300 transform hover:-translate-y-2 relative"
            >
              {canDeleteTestimonial(testimonial) && (
                <button
                  onClick={() => handleDelete(testimonial.id)}
                  className="absolute top-4 right-4 text-emerald-600 hover:text-emerald-800 transition-colors duration-200"
                  title={isAdmin ? "Delete review (Admin)" : "Delete my review"}
                >
                  <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16" />
                  </svg>
                </button>
              )}
              <div className="flex items-center mb-4">
                <StarRating rating={testimonial.rating} setRating={() => {}} editable={false} />
              </div>
              <p className="text-emerald-700 leading-relaxed mb-6 italic">
                "{testimonial.text}"
              </p>
              <div className="flex items-center">
                <div>
                  <h4 className="font-semibold text-emerald-900">
                    {testimonial.name}
                  </h4>
                  <p className="text-sm text-emerald-600">
                    {new Date(testimonial.timestamp).toLocaleDateString()}
                  </p>
                </div>
              </div>
            </div>
          ))}
        </div>

        {testimonials.length === 0 && (
          <div className="text-center py-12">
            <p className="text-emerald-600 text-lg">No reviews yet. Be the first to share your experience!</p>
          </div>
        )}

        {testimonials.length > 0 && (
          <div className="mt-16 text-center">
            <div className="bg-gradient-to-r from-emerald-500 to-emerald-700 rounded-2xl p-8 lg:p-12 text-cream">
              <div className="flex justify-center items-center mb-4">
                <StarRating rating={Math.round(testimonials.reduce((acc, t) => acc + t.rating, 0) / testimonials.length)} setRating={() => {}} editable={false} />
              </div>
              <h3 className="text-2xl font-bold mb-2">
                {(testimonials.reduce((acc, t) => acc + t.rating, 0) / testimonials.length).toFixed(1)}/5 Average Rating
              </h3>
              <p className="text-lg opacity-90 mb-6">
                Based on {testimonials.length} review{testimonials.length !== 1 ? 's' : ''} from our community
              </p>
              <div className="grid md:grid-cols-4 gap-6 text-center">
                <div>
                  <div className="text-2xl font-bold">
                    {testimonials.length > 0 ? Math.round((testimonials.filter(t => t.rating >= 4).length / testimonials.length) * 100) : 0}%
                  </div>
                  <div className="text-sm opacity-75">Satisfaction Rate</div>
                </div>
                <div>
                  <div className="text-2xl font-bold">{testimonials.length}</div>
                  <div className="text-sm opacity-75">Happy Players</div>
                </div>
                <div>
                  <div className="text-2xl font-bold">
                    {testimonials.length > 0 ? Math.round((testimonials.filter(t => t.rating >= 4).length / testimonials.length) * 100) : 0}%
                  </div>
                  <div className="text-sm opacity-75">Would Recommend</div>
                </div>
                <div>
                  <div className="text-2xl font-bold">
                    {testimonials.length > 0 ? (testimonials.reduce((acc, t) => acc + t.rating, 0) / testimonials.length).toFixed(1) : '0.0'}
                  </div>
                  <div className="text-sm opacity-75">Average Rating</div>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
    </section>
  );
};

export default Testimonials;
