import React, { useState } from 'react';

const LoginRegister = ({ onLogin }) => {
  const [phone, setPhone] = useState('');
  const [step, setStep] = useState(1); // 1: phone, 2: otp
  const [otp, setOtp] = useState('');

  const handlePhoneSubmit = (e) => {
    e.preventDefault();
    setStep(2);
  };

  const handleOtpSubmit = (e) => {
    e.preventDefault();
    if (onLogin) onLogin();
  };

  return (
    <section className="min-h-screen flex items-center justify-center bg-gradient-to-br from-khaki to-wasabi">
      <div className="bg-accent rounded-2xl shadow-xl p-8 w-full max-w-md">
        <h2 className="text-2xl font-bold text-primary mb-6 text-center">Login / Register</h2>
        {step === 1 ? (
          <form onSubmit={handlePhoneSubmit} className="space-y-6">
            <div>
              <label htmlFor="phone" className="block text-sm font-medium text-primary mb-2">
                Phone Number
              </label>
              <input
                type="tel"
                id="phone"
                name="phone"
                value={phone}
                onChange={(e) => setPhone(e.target.value)}
                required
                pattern="[0-9]{10}"
                maxLength={10}
                className="w-full px-4 py-3 border border-primary rounded-lg focus:ring-2 focus:ring-earth focus:border-transparent transition-colors duration-300 text-primary bg-khaki placeholder-primary"
                placeholder="Enter your 10-digit phone number"
              />
            </div>
            <button type="submit" className="btn-primary w-full text-lg py-3">
              Continue
            </button>
          </form>
        ) : (
          <form onSubmit={handleOtpSubmit} className="space-y-6">
            <div>
              <label htmlFor="otp" className="block text-sm font-medium text-primary mb-2">
                Enter OTP
              </label>
              <input
                type="text"
                id="otp"
                name="otp"
                value={otp}
                onChange={(e) => setOtp(e.target.value)}
                required
                pattern="[0-9]{4,6}"
                maxLength={6}
                className="w-full px-4 py-3 border border-primary rounded-lg focus:ring-2 focus:ring-earth focus:border-transparent transition-colors duration-300 text-primary bg-khaki placeholder-primary"
                placeholder="Enter the OTP you received"
              />
            </div>
            <button type="submit" className="btn-primary w-full text-lg py-3">
              Verify & Continue
            </button>
            <button type="button" className="text-primary underline mt-2" onClick={() => setStep(1)}>
              Change Phone Number
            </button>
          </form>
        )}
      </div>
    </section>
  );
};

export default LoginRegister;
