import React, { useState } from 'react';

const StarRating = ({ rating, setRating, editable }) => (
  <div className="flex items-center space-x-1">
    {[1, 2, 3, 4, 5].map((star) => (
      <button
        key={star}
        type="button"
        className={
          star <= rating
            ? 'text-yellow-400 text-2xl'
            : 'text-gray-300 text-2xl'
        }
        onClick={editable ? () => setRating(star) : undefined}
        disabled={!editable}
        aria-label={`Rate ${star} star${star > 1 ? 's' : ''}`}
      >
        â˜…
      </button>
    ))}
  </div>
);

const Testimonials = ({ testimonials, onSubmit, isLoggedIn }) => {
  const [text, setText] = useState('');
  const [rating, setRating] = useState(5);
  const [name, setName] = useState('');
  const [submitted, setSubmitted] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (text.trim() && rating) {
      onSubmit({ text, rating, name: name.trim() || 'Anonymous' });
      setText('');
      setRating(5);
      setName('');
      setSubmitted(true);
      setTimeout(() => setSubmitted(false), 2000);
    }
  };

  return (
    <section id="testimonials" className="section-padding bg-khaki">
      <div className="container-custom">
        <div className="text-center mb-16">
          <h2 className="text-3xl md:text-4xl font-bold text-primary mb-4">
            What Our Players Say
          </h2>
          <p className="text-lg text-secondary max-w-2xl mx-auto">
            Real experiences from our growing community of football enthusiasts.
          </p>
        </div>

        {/* Submission Form */}
        {isLoggedIn && (
          <div className="max-w-xl mx-auto mb-12 bg-accent rounded-2xl p-6 shadow-lg">
            <h3 className="text-xl font-semibold text-primary mb-4">Share Your Experience</h3>
            <form onSubmit={handleSubmit} className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-primary mb-1">Your Testimonial</label>
                <textarea
                  value={text}
                  onChange={(e) => setText(e.target.value)}
                  required
                  rows={3}
                  className="w-full px-4 py-2 border border-primary rounded-lg focus:ring-2 focus:ring-earth focus:border-transparent transition-colors duration-300 text-primary bg-khaki placeholder-primary"
                  placeholder="Write your experience..."
                />
              </div>
              <div className="flex items-center space-x-4">
                <label className="text-primary font-medium">Your Rating:</label>
                <StarRating rating={rating} setRating={setRating} editable={true} />
              </div>
              <div>
                <label className="block text-sm font-medium text-primary mb-1">Your Name (optional)</label>
                <input
                  type="text"
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                  className="w-full px-4 py-2 border border-primary rounded-lg focus:ring-2 focus:ring-earth focus:border-transparent transition-colors duration-300 text-primary bg-khaki placeholder-primary"
                  placeholder="Enter your name or leave blank for anonymous"
                />
              </div>
              <button type="submit" className="btn-primary w-full text-lg py-2">
                Submit Testimonial
              </button>
              {submitted && <div className="text-green-700 text-center mt-2">Thank you for your feedback!</div>}
            </form>
          </div>
        )}

        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
          {testimonials.map((testimonial, index) => (
            <div
              key={index}
              className="bg-wasabi rounded-2xl p-8 hover:shadow-lg transition-all duration-300 transform hover:-translate-y-2"
            >
              <div className="flex items-center mb-4">
                <StarRating rating={testimonial.rating} setRating={() => {}} editable={false} />
              </div>
              <p className="text-secondary-700 leading-relaxed mb-6 italic">
                "{testimonial.text}"
              </p>
              <div className="flex items-center">
                <div>
                  <h4 className="font-semibold text-secondary-900">
                    {testimonial.name}
                  </h4>
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* Overall Rating */}
        <div className="mt-16 text-center">
          <div className="bg-gradient-to-r from-primary-500 to-primary-700 rounded-2xl p-8 lg:p-12 text-white">
            <div className="flex justify-center items-center mb-4">
              <StarRating rating={5} setRating={() => {}} editable={false} />
            </div>
            <h3 className="text-2xl font-bold mb-2">4.9/5 Average Rating</h3>
            <p className="text-lg opacity-90 mb-6">
              Based on 500+ reviews from our community
            </p>
            <div className="grid md:grid-cols-4 gap-6 text-center">
              <div>
                <div className="text-2xl font-bold">98%</div>
                <div className="text-sm opacity-75">Satisfaction Rate</div>
              </div>
              <div>
                <div className="text-2xl font-bold">500+</div>
                <div className="text-sm opacity-75">Happy Players</div>
              </div>
              <div>
                <div className="text-2xl font-bold">95%</div>
                <div className="text-sm opacity-75">Would Recommend</div>
              </div>
              <div>
                <div className="text-2xl font-bold">4.9</div>
                <div className="text-sm opacity-75">Average Rating</div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Testimonials;
