import React from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';

const Navbar = ({ 
  isMenuOpen, 
  setIsMenuOpen, 
  isAdmin, 
  onToggleAdmin, 
  showAdminLogin, 
  adminPassword, 
  setAdminPassword, 
  onAdminLogin, 
  onAdminCancel 
}) => {
  const navigate = useNavigate();
  const location = useLocation();

  const navItems = [
    { name: 'Upcoming Matches', href: '/matches', isLink: true },
    { name: 'Testimonials', href: '#testimonials' },
    { name: 'Gallery', href: '#gallery' },
    { name: 'Payment Gateway', href: '#payment' },
    { name: 'Login/Register', href: '/login', isLink: true },
  ];

  // Scroll to section, even from other pages
  const handleScrollOrNavigate = (href) => {
    if (location.pathname !== '/') {
      sessionStorage.setItem('scrollTo', href);
      navigate('/');
      setIsMenuOpen(false);
    } else {
      const element = document.querySelector(href);
      if (element) {
        element.scrollIntoView({ behavior: 'smooth' });
      }
      setIsMenuOpen(false);
    }
  };

  React.useEffect(() => {
    // On homepage load, check if we need to scroll to a section
    if (location.pathname === '/') {
      const scrollTo = sessionStorage.getItem('scrollTo');
      if (scrollTo) {
        setTimeout(() => {
          const element = document.querySelector(scrollTo);
          if (element) {
            element.scrollIntoView({ behavior: 'smooth' });
          }
        }, 200);
        sessionStorage.removeItem('scrollTo');
      }
    }
  }, [location.pathname]);

  return (
    <nav className="fixed top-0 left-0 right-0 z-50 bg-emerald/95 backdrop-blur-sm shadow-lg">
      <div className="container-custom">
        <div className="flex items-center justify-between h-16">
          {/* Logo */}
          <div className="flex items-center space-x-2">
            <div className="w-10 h-10 bg-gradient-to-br from-emerald-600 to-emerald rounded-lg flex items-center justify-center">
              <span className="text-cream font-bold text-xl">K</span>
            </div>
            <span className="text-2xl font-bold text-cream">Kickora</span>
          </div>

          {/* Desktop Navigation */}
          <div className="hidden md:flex items-center space-x-8">
            {navItems.map((item) =>
              item.isLink ? (
                <Link
                  key={item.name}
                  to={item.href}
                  className={'text-cream hover:text-emerald-200 font-medium transition-colors duration-300'}
                >
                  {item.name}
                </Link>
              ) : (
                <button
                  key={item.name}
                  onClick={() => handleScrollOrNavigate(item.href)}
                  className="text-cream hover:text-emerald-200 font-medium transition-colors duration-300"
                >
                  {item.name}
                </button>
              )
            )}
            <button
              onClick={() => handleScrollOrNavigate('#booking')}
              className="btn-primary"
            >
              Book a Slot
            </button>
            <button
              onClick={onToggleAdmin}
              className={`px-4 py-2 rounded-lg font-medium transition-colors duration-300 ${
                isAdmin 
                  ? 'bg-emerald-800 text-cream hover:bg-emerald-900' 
                  : 'bg-emerald-600 text-cream hover:bg-emerald-700'
              }`}
              title={isAdmin ? "Exit Admin Mode" : "Enter Admin Mode"}
            >
              {isAdmin ? "Admin Mode" : "Admin"}
            </button>
          </div>

          {/* Mobile Menu Button */}
          <button
            onClick={() => setIsMenuOpen(!isMenuOpen)}
            className="md:hidden p-2 rounded-lg hover:bg-gray-100 transition-colors duration-300"
          >
            <svg
              className="w-6 h-6"
              fill="none"
              stroke="currentColor"
              viewBox="0 0 24 24"
            >
              {isMenuOpen ? (
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d="M6 18L18 6M6 6l12 12"
                />
              ) : (
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d="M4 6h16M4 12h16M4 18h16"
                />
              )}
            </svg>
          </button>
        </div>

        {/* Mobile Menu */}
        {isMenuOpen && (
          <div className="md:hidden py-4 border-t border-gray-200">
            <div className="flex flex-col space-y-4">
              {navItems.map((item) =>
                item.isLink ? (
                  <Link
                    key={item.name}
                    to={item.href}
                    className={'text-left text-cream hover:text-emerald-200 font-medium transition-colors duration-300'}
                    onClick={() => setIsMenuOpen(false)}
                  >
                    {item.name}
                  </Link>
                ) : (
                  <button
                    key={item.name}
                    onClick={() => handleScrollOrNavigate(item.href)}
                    className="text-left text-cream hover:text-emerald-200 font-medium transition-colors duration-300"
                  >
                    {item.name}
                  </button>
                )
              )}
              <button
                onClick={() => handleScrollOrNavigate('#booking')}
                className="btn-primary w-full"
              >
                Book a Slot
              </button>
              <button
                onClick={onToggleAdmin}
                className={`px-4 py-2 rounded-lg font-medium transition-colors duration-300 ${
                  isAdmin 
                    ? 'bg-emerald-800 text-cream hover:bg-emerald-900' 
                    : 'bg-emerald-600 text-cream hover:bg-emerald-700'
                }`}
                title={isAdmin ? "Exit Admin Mode" : "Enter Admin Mode"}
              >
                {isAdmin ? "Admin Mode" : "Admin"}
              </button>
            </div>
          </div>
        )}
      </div>

      {/* Admin Login Modal */}
      {showAdminLogin && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-cream rounded-2xl p-8 max-w-md w-full mx-4">
            <h3 className="text-2xl font-bold text-emerald mb-6 text-center">
              Admin Access Required
            </h3>
            <div className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-emerald mb-2">
                  Admin Password
                </label>
                <input
                  type="password"
                  value={adminPassword}
                  onChange={(e) => setAdminPassword(e.target.value)}
                  onKeyPress={(e) => e.key === 'Enter' && onAdminLogin()}
                  className="w-full px-4 py-3 border border-emerald rounded-lg focus:ring-2 focus:ring-emerald focus:border-transparent transition-colors duration-300 text-emerald bg-cream placeholder-emerald-500"
                  placeholder="Enter admin password"
                  autoFocus
                />
              </div>
              <div className="flex space-x-3">
                <button
                  onClick={onAdminLogin}
                  className="flex-1 bg-emerald text-cream py-3 px-4 rounded-lg font-medium hover:bg-emerald-600 transition-colors duration-300"
                >
                  Login
                </button>
                <button
                  onClick={onAdminCancel}
                  className="flex-1 bg-emerald-100 text-emerald-700 py-3 px-4 rounded-lg font-medium hover:bg-emerald-200 transition-colors duration-300"
                >
                  Cancel
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </nav>
  );
};

export default Navbar;
