import React from 'react';
import { Link } from 'react-router-dom';

const Navbar = ({ isMenuOpen, setIsMenuOpen }) => {
  const navItems = [
    { name: 'Upcoming Matches', href: '/matches', isLink: true },
    { name: 'Testimonials', href: '#testimonials' },
    { name: 'Gallery', href: '#gallery' },
    { name: 'Payment Gateway', href: '#payment' },
    { name: 'Login/Register', href: '/login', isLink: true },
  ];

  const scrollToSection = (href) => {
    const element = document.querySelector(href);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
    setIsMenuOpen(false);
  };

  return (
    <nav className="fixed top-0 left-0 right-0 z-50 bg-primary/95 backdrop-blur-sm shadow-lg">
      <div className="container-custom">
        <div className="flex items-center justify-between h-16">
          {/* Logo */}
          <div className="flex items-center space-x-2">
            <div className="w-10 h-10 bg-gradient-to-br from-earth to-primary rounded-lg flex items-center justify-center">
              <span className="text-accent font-bold text-xl">K</span>
            </div>
            <span className="text-2xl font-bold text-accent">Kickora</span>
          </div>

          {/* Desktop Navigation */}
          <div className="hidden md:flex items-center space-x-8">
            {navItems.map((item) =>
              item.isLink ? (
                <Link
                  key={item.name}
                  to={item.href}
                  className={'text-accent hover:text-earth font-medium transition-colors duration-300'}
                >
                  {item.name}
                </Link>
              ) : (
                <button
                  key={item.name}
                  onClick={() => scrollToSection(item.href)}
                  className="text-accent hover:text-earth font-medium transition-colors duration-300"
                >
                  {item.name}
                </button>
              )
            )}
            <button
              onClick={() => scrollToSection('#booking')}
              className="btn-primary"
            >
              Book a Slot
            </button>
          </div>

          {/* Mobile Menu Button */}
          <button
            onClick={() => setIsMenuOpen(!isMenuOpen)}
            className="md:hidden p-2 rounded-lg hover:bg-gray-100 transition-colors duration-300"
          >
            <svg
              className="w-6 h-6"
              fill="none"
              stroke="currentColor"
              viewBox="0 0 24 24"
            >
              {isMenuOpen ? (
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d="M6 18L18 6M6 6l12 12"
                />
              ) : (
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d="M4 6h16M4 12h16M4 18h16"
                />
              )}
            </svg>
          </button>
        </div>

        {/* Mobile Menu */}
        {isMenuOpen && (
          <div className="md:hidden py-4 border-t border-gray-200">
            <div className="flex flex-col space-y-4">
              {navItems.map((item) =>
                item.isLink ? (
                  <Link
                    key={item.name}
                    to={item.href}
                    className={'text-left text-accent hover:text-earth font-medium transition-colors duration-300'}
                    onClick={() => setIsMenuOpen(false)}
                  >
                    {item.name}
                  </Link>
                ) : (
                  <button
                    key={item.name}
                    onClick={() => scrollToSection(item.href)}
                    className="text-left text-accent hover:text-earth font-medium transition-colors duration-300"
                  >
                    {item.name}
                  </button>
                )
              )}
              <button
                onClick={() => scrollToSection('#booking')}
                className="btn-primary w-full"
              >
                Book a Slot
              </button>
            </div>
          </div>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
