import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate, useLocation } from 'react-router-dom';
import Navbar from './components/Navbar';
import Hero from './components/Hero';
import VisionMission from './components/VisionMission';
import Features from './components/Features';
import Testimonials from './components/Testimonials';
import Gallery from './components/Gallery';
import BookingForm from './components/BookingForm';
import Footer from './components/Footer';
import LoginRegister from './components/LoginRegister';
import Matches from './components/Matches';

// Simple login state for demo
const AppRoutes = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [pendingMatch, setPendingMatch] = useState(null);
  const navigate = useNavigate();
  const location = useLocation();

  // Handler for booking a match
  const handleBook = (match) => {
    if (!isLoggedIn) {
      setPendingMatch(match);
      navigate('/login');
    } else {
      // In a real app, show booking confirmation or form here
      alert(`Booking confirmed for ${match.type} at ${match.location} on ${match.date} at ${match.time}`);
    }
  };

  // Handler for login/register success
  const handleLogin = () => {
    setIsLoggedIn(true);
    if (pendingMatch) {
      alert(`Booking confirmed for ${pendingMatch.type} at ${pendingMatch.location} on ${pendingMatch.date} at ${pendingMatch.time}`);
      setPendingMatch(null);
      navigate('/matches');
    } else {
      navigate('/');
    }
  };

  return (
    <>
      <Navbar isMenuOpen={false} setIsMenuOpen={() => {}} />
      <Routes>
        <Route
          path="/"
          element={
            <div className="App">
              <Hero />
              <VisionMission />
              <Features />
              <Testimonials />
              <Gallery />
              <BookingForm />
              <Footer />
            </div>
          }
        />
        <Route path="/login" element={<LoginRegister onLogin={handleLogin} />} />
        <Route path="/matches" element={<Matches onBook={handleBook} />} />
      </Routes>
    </>
  );
};

function App() {
  return (
    <Router>
      <AppRoutes />
    </Router>
  );
}

export default App;
