import React from 'react';

const matches = [
  {
    id: 1,
    date: '2024-06-10',
    time: '6:00 PM',
    location: 'Andheri West Turf',
    type: '5-a-side',
    price: '₹299/player',
    playersLeft: 22,
  },
  {
    id: 2,
    date: '2024-06-11',
    time: '7:00 PM',
    location: 'Bandra East Elite Turf',
    type: '7-a-side',
    price: '₹399/player',
    playersLeft: 22,
  },
  {
    id: 3,
    date: '2024-06-12',
    time: '8:00 PM',
    location: 'Powai Pro Turf',
    type: '11-a-side',
    price: '₹499/player',
    playersLeft: 22,
  },
  {
    id: 4,
    date: '2024-06-13',
    time: '6:30 PM',
    location: 'Juhu Champion Turf',
    type: '5-a-side',
    price: '₹299/player',
    playersLeft: 22,
  },
  {
    id: 5,
    date: '2024-06-14',
    time: '7:30 PM',
    location: 'Andheri West Turf',
    type: '7-a-side',
    price: '₹399/player',
    playersLeft: 22,
  },
];

const Matches = ({ matches, onBook }) => {
  return (
    <section className="min-h-screen bg-gradient-to-br from-khaki to-wasabi pt-24 pb-16">
      <div className="container-custom">
        <h2 className="text-3xl md:text-4xl font-bold text-primary mb-10 text-center">Upcoming Matches This Week</h2>
        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
          {matches.map((match) => (
            <div key={match.id} className="bg-accent rounded-2xl shadow-lg p-6 flex flex-col justify-between">
              <div>
                <div className="flex items-center justify-between mb-2">
                  <span className="text-lg font-semibold text-primary">{match.date}</span>
                  <span className="text-earth font-bold">{match.time}</span>
                </div>
                <div className="mb-2 text-secondary font-medium">{match.location}</div>
                <div className="mb-2 text-primary">11-a-side</div>
                <div className="mb-2 text-earth font-semibold">Players Left: {match.playersLeft} / 22</div>
                <div className="mb-4 text-earth font-semibold">₹299/player</div>
              </div>
              <button
                className="btn-primary mt-2 disabled:opacity-60 disabled:cursor-not-allowed"
                onClick={() => onBook(match)}
                disabled={match.playersLeft === 0}
              >
                {match.playersLeft === 0 ? 'Full' : 'Book'}
              </button>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Matches;
